package stocksystem; import java.util.*; public class Inventario {private ArrayList<Producto> productos=new ArrayList<>();public void agregarProducto(Producto p){productos.add(p);}public void listarProductos(){ for(Producto p:productos)p.mostrarInfo();}public Producto buscarProductoPorId(String id){ for(Producto p:productos)if(p.getId().equals(id))return p; return null;}public void eliminarProducto(String id){productos.removeIf(p->p.getId().equals(id));}public void actualizarStock(String id,int c){Producto p=buscarProductoPorId(id); if(p!=null)p.setCantidad(c);}public void filtrarPorCategoria(CategoriaProducto cat){for(Producto p:productos)if(p.getCategoria()==cat)p.mostrarInfo();}public int obtenerTotalStock(){int t=0;for(Producto p:productos)t+=p.getCantidad();return t;}public Producto obtenerProductoConMayorStock(){return productos.stream().max(Comparator.comparingInt(Producto::getCantidad)).orElse(null);}public void filtrarProductosPorPrecio(double min,double max){for(Producto p:productos)if(p.getPrecio()>=min&&p.getPrecio()<=max)p.mostrarInfo();}public void mostrarCategoriasDisponibles(){for(CategoriaProducto c:CategoriaProducto.values())System.out.println(c+" - "+c.getDescripcion());}}