package stocksystem; public class Producto {private String id,nombre;private double precio;private int cantidad;private CategoriaProducto categoria;public Producto(String id,String nombre,double precio,int cantidad,CategoriaProducto cat){this.id=id;this.nombre=nombre;this.precio=precio;this.cantidad=cantidad;this.categoria=cat;}public String getId(){return id;}public int getCantidad(){return cantidad;}public double getPrecio(){return precio;}public CategoriaProducto getCategoria(){return categoria;}public void setCantidad(int c){this.cantidad=c;}public void mostrarInfo(){System.out.println("ID:"+id+" Nombre:"+nombre+" Precio:"+precio+" Cant:"+cantidad+" Cat:"+categoria+" ("+categoria.getDescripcion()+")");}}